from collections import Counter


class Solution:
    def solve(self, A, B):
        y_in_x = {}
        num_rectangles = 0

        for i in range(len(A)):
            if A[i] in y_in_x:
                y_in_x[A[i]].add(B[i])
            else:
                y_in_x[A[i]] = {B[i]}

        distinct_x = list(set(A))

        for i in range(len(distinct_x)):
            for j in range(i + 1, len(distinct_x)):
                common_y = len(y_in_x[distinct_x[i]].intersection(y_in_x[distinct_x[j]]))
                num_rectangles += ((common_y - 1) * common_y) // 2
        return num_rectangles

